import './App.css';
import React, { useState } from "react";

function App() {
  const [balance, setBalance] = useState(100);
  const [expenses, setExpenses] = useState([]);
  const [expenseName, setExpenseName] = useState();
  const [expenseAmount, setExpenseAmount] = useState();
  const [expenseDate, setExpenseDate] = useState();

  const handleAddExpense = (e) => {
    e.preventDefault();

    if (!expenseName || !expenseAmount || !expenseDate){
      alert("Please fill out all fields!");
      return;
    }

    const newExpense = {
      id: Date.now(),
      name: expenseName,
      amount: expenseAmount,
      date: expenseDate
    };

    setExpenses([...expenses, newExpense]);
    setBalance(balance - parseFloat(expenseAmount));

    setExpenseName('');
    setExpenseAmount('');
    setExpenseDate('');
  };

  const handleDeleteExpense = (id, amount) => {
    setExpenses(expenses.filter((expense) => expense.id != id));
    setBalance(balance+amount);
  }


  return (
    <div className="container">
      <h1 className="header">My Expense Tracker</h1>
      <div className="balance">
        <h2>Current Balance:</h2>
        <h3 className="balance-dollars">{balance}$</h3>
      </div>
      <div className="main-content">
        <div className="expense-form">
          <h3 className="expense-header">Add Expense</h3>
          <form className="add-expense">
            <div className="form-group">
              <label htmlFor="expense-name">Name:</label>
              <input 
              type="text" 
              id="expense-name"
              name="expense-name" 
              value={expenseName}
              onChange={(e) => setExpenseName(e.target.value)}/>
            </div>
            <div className="form-group">
              <label htmlFor="expense-amount">Amount:</label>
              <input 
              type="text" 
              id="expense-amount" 
              name="expense-amount" 
              value={expenseAmount}
              onChange={(e) => setExpenseAmount(e.target.value)} />
            </div>
            <div className="form-group">
              <label htmlFor="expense-date">Date:</label>
              <input 
              type="date" 
              id="expense-date" 
              name="expense-date" 
              value={expenseDate}
              onChange={(e) => setExpenseDate(e.target.value)}/>
            </div>
            <div className="form-group">
              <button 
              className='add-expense-btn'
              onClick={handleAddExpense}
              >Add Expense</button>
            </div>
          </form>
        </div>
        <div className="expense-container">
        <ul className="expense-list">
            {expenses.map((expense) => (
              <li className="expense" key={expense.id}>
                {expense.name} - ${expense.amount} on {expense.date}
                <button className="delete-expense-btn"  onClick={() => handleDeleteExpense(expense.id, expense.amount)}><i class="fa fa-trash"></i></button>
              </li>
            ))}
          </ul>
        </div>
      </div>
    </div>
  );
}

export default App;
