import './App.css';
import React, { useEffect, useState } from "react";

function App() {
  const [balance, setBalance] = useState(200); // Initial balance
  const [expenses, setExpenses] = useState([]); // Initialize as an empty array
  const [expenseName, setExpenseName] = useState(""); // Initialize as empty string
  const [expenseAmount, setExpenseAmount] = useState(""); // Initialize as empty string
  const [expenseDate, setExpenseDate] = useState(""); // Initialize as empty string

  // Load saved data from localStorage on component mount
  useEffect(() => {
    const savedExpenses = localStorage.getItem("expenses");
    const savedBalance = localStorage.getItem("balance");

    if (savedExpenses) {
      setExpenses(JSON.parse(savedExpenses)); // Parse and set expenses
    }

    if (savedBalance) {
      setBalance(parseFloat(savedBalance)); // Parse and set balance
    }
  }, []);

  // Save expenses and balance to localStorage whenever they change
  useEffect(() => {
    localStorage.setItem("expenses", JSON.stringify(expenses));
    localStorage.setItem("balance", balance.toString());
  }, [expenses, balance]);

  const handleAddExpense = (e) => {
    e.preventDefault();

    if (!expenseName || !expenseAmount || !expenseDate) {
      alert("Please fill out all fields!");
      return;
    }

    const newExpense = {
      id: Date.now(),
      name: expenseName,
      amount: parseFloat(expenseAmount), // Ensure amount is stored as a number
      date: expenseDate,
    };

    setExpenses([...expenses, newExpense]); // Add the new expense
    setBalance(balance - parseFloat(expenseAmount)); // Update the balance

    // Clear input fields
    setExpenseName('');
    setExpenseAmount('');
    setExpenseDate('');
  };

  const handleDeleteExpense = (id, amount) => {
    setExpenses(expenses.filter((expense) => expense.id !== id)); // Remove expense by ID
    setBalance(balance + parseFloat(amount)); // Restore balance
  };

  return (
    <div className="container">
      <h1 className="header">My Expense Tracker</h1>
      <div className="balance">
        <h2>Current Balance:</h2>
        <h3 className="balance-dollars">{balance}$</h3>
      </div>
      <div className="main-content">
        <div className="expense-form">
          <h3 className="expense-header">Add Expense</h3>
          <form className="add-expense" onSubmit={handleAddExpense}>
            <div className="form-group">
              <label htmlFor="expense-name">Name:</label>
              <input
                type="text"
                id="expense-name"
                name="expense-name"
                value={expenseName}
                onChange={(e) => setExpenseName(e.target.value)}
                placeholder="Expense Name"
                required
              />
            </div>
            <div className="form-group">
              <label htmlFor="expense-amount">Amount:</label>
              <input
                type="number"
                id="expense-amount"
                name="expense-amount"
                value={expenseAmount}
                onChange={(e) => setExpenseAmount(e.target.value)}
                placeholder="Amount (e.g., 50)"
                required
              />
            </div>
            <div className="form-group">
              <label htmlFor="expense-date">Date:</label>
              <input
                type="date"
                id="expense-date"
                name="expense-date"
                value={expenseDate}
                onChange={(e) => setExpenseDate(e.target.value)}
                required
              />
            </div>
            <div className="form-group">
              <button
                type="submit"
                className="add-expense-btn"
              >
                Add Expense
              </button>
            </div>
          </form>
        </div>
        <div className="expense-container">
          <ul className="expense-list">
            {expenses.map((expense) => (
              <li className="expense" key={expense.id}>
                {expense.name} - ${expense.amount} on {expense.date}
                <button
                  className="delete-expense-btn"
                  onClick={() => handleDeleteExpense(expense.id, expense.amount)}
                >
                  <i className="fa fa-trash"></i>
                </button>
              </li>
            ))}
          </ul>
        </div>
      </div>
    </div>
  );
}

export default App;
