import './App.css';
import React, { useState } from "react";

function App() {
  const [balance, setBalance] = useState();
  const [expenses, setExpenses] = useState();
  const [expenseName, setExpenseName] = useState();
  const [expenseAmount, setExpenseAmount] = useState();
  const [expenseDate, setExpenseDate] = useState();

  const handleAddExpense = (e) => {
    e.preventDefault();

    if (!expenseName || !expenseAmount || !expenseDate){
      alert("Please fill out all fields!");
      return;
    }

    const newExpense = {
      id: Date.now(),
      name: expenseName,
      amount: expenseAmount,
      date: expenseDate
    };

    setExpenses([...expenses, newExpense]);
    setBalance(balance - parseFloat(expenseAmount));

    setExpenseName('');
    setExpenseAmount('');
    setExpenseDate('');
  }

  return (
    <div className="container">
      <h1 className="header">My Expense Tracker</h1>
      <div className="balance">
        <h2>Current Balance:</h2>
        <h3 className="balance-dollars">20$</h3>
      </div>
      <div className="main-content">
        <div className="expense-form">
          <h3 className="expense-header">Add Expense</h3>
          <form className="add-expense">
            <div className="form-group">
              <label htmlFor="expense-name">Name:</label>
              <input type="text" id="expense-name" name="expense-name" onChange={(e) => setExpenseName(e.target.value)}/>
            </div>
            <div className="form-group">
              <label htmlFor="expense-amount">Amount:</label>
              <input type="text" id="expense-amount" name="expense-amount" onChange={(e) => setExpenseAmount(e.target.value)} />
            </div>
            <div className="form-group">
              <label htmlFor="expense-date">Date:</label>
              <input type="date" id="expense-date" name="expense-date" onChange={(e) => setExpenseDate(e.target.value)}/>
            </div>
            <div className="form-group">
              <button className='add-expense-btn'>Add Expense</button>
            </div>
          </form>
        </div>
        <div className="expense-container">
          <ul className="expense-list">
            <li className="expense">Expense 1</li>
            <li className="expense">Expense 2</li>
            <li className="expense">Expense 3</li>
            <li className="expense">Expense 4</li>
            <li className="expense">Expense 5</li>
          </ul>
        </div>
      </div>
    </div>
  );
}

export default App;
